import React from 'react';
import '../App.css';  // Ensure the CSS file is correctly imported
import hotels from '../data';



const HotelDetail = () => {
  return (
    <div className="grid">
      {/* Main Image on the left */}
      <div className="hero-image-container-hotel-widok-1" style={{ backgroundImage: `url(${mainImage})` }}>
        {/* This div handles the large main image using background styles */}
      </div>

      {/* Hotel details and smaller images container on the right */}
      <div className="hero-details">
        <h1 className="title-large">{hotelData.name}</h1>
        <div className="text-middle">
          <p>Location: {hotelData.location}</p>
          <p>Local category: {hotelData.category}</p>
          <p>Price: {hotelData.price}/night</p>
          <p>Description: {hotelData.description}</p>
        </div>

        {/* Smaller images below the details */}
        <div className="hero-cards">
          <div className="card-image" style={{ backgroundImage: `url(${image1})` }}></div>
          <div className="card-image" style={{ backgroundImage: `url(${image2})` }}></div>
        </div>
      </div>
    </div>
  );
}

export default HotelDetail;
