import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  // Ensure the CSS file is correctly imported
import hotels from '../data'; // Import the hotels data

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <div className="grid" style={{ display: 'flex', flexDirection: 'row', height: '100vh', alignItems: 'stretch' }}>
            {/* Main image on the left with the hotel name overlay */}
            <div style={{ width: '60%', position: 'relative', overflow: 'hidden' }}>
                <img src={hotel.image} alt={hotel.name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                <div style={{
                    position: 'absolute',
                    bottom: '10%',
                    left: '5%',
                    right: '5%',
                    color: 'white',
                    fontSize: '2.5rem',
                    textAlign: 'center',
                    textShadow: '2px 2px 8px rgba(0,0,0,0.8)' // Enhanced text shadow for readability
                }}>
                    {hotel.name}
                </div>
            </div>

            {/* Hotel details on the right */}
            <div style={{ width: '40%', padding: '2rem', backgroundColor: 'white', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
                <p><strong>Location:</strong> {hotel.location}</p>
                <p><strong>Description:</strong> {hotel.description}</p>
                <p><strong>Price per night:</strong> {hotel.price}</p>
                <p><strong>Rating:</strong> {hotel.rating} stars</p>
            </div>
        </div>
    );
}

export default HotelDetail;
