import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  // Ensure the CSS file is correctly imported
import hotels from '../data'; // Import the hotels data

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
            {/* Hotel name positioned above the image */}
            <div style={{
                textAlign: 'center',
                fontSize: '3rem',
                color: 'black', // You can adjust the color based on your theme
                padding: '20px 0',
                backgroundColor: 'white', // Adds a background color to the title for better readability
                width: '100%' // Ensures the title spans the full width
            }}>
                {hotel.name}
            </div>

            {/* Main image and details container */}
            <div style={{ display: 'flex', flexDirection: 'row', flexGrow: 1, alignItems: 'stretch' }}>
                {/* Main image on the left */}
                <div style={{ width: '60%', overflow: 'hidden' }}>
                    <img src={hotel.image} alt={hotel.name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                </div>

                {/* Hotel details on the right */}
                <div style={{ width: '40%', padding: '2rem', backgroundColor: 'white', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
                    <p><strong>Location:</strong> {hotel.location}</p>
                    <p><strong>Description:</strong> {hotel.description}</p>
                    <p><strong>Price per night:</strong> {hotel.price}</p>
                    <p><strong>Rating:</strong> {hotel.rating} stars</p>
                </div>
            </div>
        </div>
    );
}

export default HotelDetail;
