import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  
import hotels from '../data'; 

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <>

        <div className='grid-hotelDetail-row'>


            <div className="grid-hotelDetail" style={{ display: 'flex', flexDirection: 'row', width: '100%', height: '100%',}}>
                <div className="hotel-detail-image-left" style={{ width: '40%', height: '40', backgroundImage: `url(${hotel.image})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>

                </div>

                        <div style={{ width: '50%',  display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                            <div>
                                <p><strong style={{ fontWeight: 'bold' }}>Location:</strong> {hotel.location}</p>
                                <p><strong style={{ fontWeight: 'bold' }}>Local category:</strong> {hotel.rating} </p>
                                <p><strong style={{ fontWeight: 'bold' }}>Price per night:</strong> {hotel.price}</p>
                                <p><strong style={{ fontWeight: 'bold' }}>Description:</strong> <div>{hotel.description}</div></p>
                            </div>


                            <div  style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px'}}>
                                <img src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover',borderRadius:'32px' }} />
                                <img  src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover' ,borderRadius:'32px'}} />
                            </div>

                        </div>
            </div>
        </div>    
        </>
    );
}

export default HotelDetail;
