import React from 'react';
import { useParams, Link } from 'react-router-dom';
import hotels from '../data';


const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    if (!hotel) {
        return <p>Hotel not found</p>;
    }

    return (
        <section className="hotel-detail grid">
            <div className="header-navigation">
                <Link to="/" className="back-link nav-link">Back to list</Link>
                <h1 className="title-large">Serene Retreat</h1>
                <button className="button primary">Log out</button>
            </div>
            <div className="hotel-content">
                <div className="hero-section">
                    <div className="hero-image-container" style={{ backgroundImage: `url(${hotel.image})` }}>
                        <div className="chip-heart-container">
                            <div className="chip">Add to favorites</div>
                            <div className="heart">
                                <svg width="24" height="24" fill="currentColor">
                                    <path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/>
                                </svg>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="hotel-info">
                    <p className="hotel-location text-small"><strong>Location:</strong> {hotel.location}</p>
                    <p className="hotel-category text-small"><strong>Local category:</strong> {hotel.rating}</p>
                    <p className="hotel-price text-small"><strong>Price:</strong> {hotel.price}</p>
                    <p className="hotel-description text-small"><strong>Description:</strong> {hotel.description}</p>
                    <button className="button primary">Contact</button>
                </div>
            </div>
        </section>
    );
};

export default HotelDetail;
