// App.js
import React, { useState } from 'react';
import '../HotelDetails.css';
import hotelData from '../data.js'; // Make sure to adjust the import path as needed

function App() {
  const [isFavorite, setIsFavorite] = useState(false);

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
  }

  return (
    <div className="app">
      <header className="header">
        <h1>{hotelData.title}</h1>
        {/* Additional navigation or header elements */}
      </header>

      <main className="main-content">
        <div className="main-image-container">
          <img src={hotelData.images.main} alt="Main View of Serene Retreat" className="main-image"/>
          <button className={`favorite-button ${isFavorite ? 'is-favorite' : ''}`} onClick={toggleFavorite}>
            {isFavorite ? '♥ Remove from Favorites' : '♡ Add to Favorites'}
          </button>
        </div>
        <div className="hotel-details">
          <h2>{hotelData.location}</h2>
          <p>Category: {hotelData.category}</p>
          <p>Price: {hotelData.price}</p>
          <p>{hotelData.description}</p>
          <button className="contact-button">Contact</button>
        </div>
      </main>

      <footer className="footer">
        {/* Footer content */}
      </footer>
    </div>
  );
}

export default App;
