import React from 'react';
import '../App.css';  // Ensure the CSS file is correctly imported

const HotelDetail = () => {
  return (
    <div className="grid">
      {/* Hero image container on the left */}
      <div className="hero-image-container-hotel-widok-1">
        {/* No specific content in the div as the image will be handled via CSS */}
      </div>

      {/* Hotel details and additional images container */}
      <div className="hero-details">
        <h1 className="title-large">Serene Retreat</h1>
        <div className="text-middle">
          <p>Location: Madrid</p>
          <p>Local category: ★★★★☆</p>
          <p>Price: 760€/night</p>
          <p>Description: Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam nec leo ligula. Etiam fermentum est in laoreet egestas.</p>
        </div>
        {/* Button to reveal more images */}
        <button className="button primary" onClick={() => { 
            document.querySelector('.additional-images').style.display = 'flex';
          }}>Add to Favorites</button>

        {/* Additional images hidden by default, shown on button click */}
        <div className="additional-images hidden">
          <div className="card-image" style={{backgroundImage: "url('./Assets/heroSmall1.jpeg')"}}>
            {/* Optionally, you could have some overlay content here */}
          </div>
          <div className="card-image" style={{backgroundImage: "url('./Assets/heroSmall2.jpg')"}}>
            {/* Optionally, you could have some overlay content here */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default HotelDetail;
