import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  
import hotels from '../data'; 

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <>

        <div className='grid-hotelDetail-row'>
            <div id='hero' className="hero-welcome">
                    <p className="title-large">{hotel.name}</p>
            </div>

            <div className="grid-hotelDetail" style={{ display: 'flex', flexDirection: 'row', width: '100%', height: '100%',}}>

                <div className="hotel-detail-image-left" style={{  backgroundImage: `url(${hotel.image})`, backgroundSize: 'cover', backgroundPosition: 'center' }}></div>

                        <div className='grid-hotelDetail-row-right-column' style={{ width: '50%',  display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                                <div className='hotelDetail-text-row'>
                                    <p style={{paddingBottom: '32px'}}><strong >Location:</strong> {hotel.location}</p>
                                    <p style={{paddingBottom: '32px'}}><strong >Local category:</strong> {hotel.rating} </p>
                                    <p><strong >Price per night:</strong> {hotel.price}</p>
                                    <p><strong >Description:</strong> <div>{hotel.description}</div></p>
                                </div>


                                <div  className='hotelDetail-image-row' style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px'}}>
                                    <img src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover',borderRadius:'32px' }} />
                                    <img  src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover' ,borderRadius:'32px'}} />
                                </div>
                        </div>
                </div>
        </div>    
        </>
    );
}

export default HotelDetail;
