import React from 'react';
import './App.css';
import hotelData from './data.js';  // Importing data from data.js

function App() {
  return (
    <div className="app">
      <header className="header">
        <h1>{hotelData.title}</h1>
        {/* Optional: Other header elements */}
      </header>

      <main className="main-content">
        <div className="main-image">
          <img src={hotelData.images.main} alt="Main View of Serene Retreat" />
        </div>
        <div className="info">
          <div className="details">
            <h2>Location: {hotelData.location}</h2>
            <h3>Local category: {hotelData.category}</h3>
            <p>Price: {hotelData.price}</p>
            <p>Description: {hotelData.description}</p>
          </div>
          <button className="contact-button">Contact us</button>
        </div>
        <div className="additional-images">
          {hotelData.images.additional.map((image, index) => (
            <img key={index} src={image} alt={`Additional View ${index + 1}`} />
          ))}
        </div>
      </main>

      <footer className="footer">
        {/* Footer content */}
      </footer>
    </div>
  );
}

export default App;
