import React from 'react';
import { useParams, Link } from 'react-router-dom';
import hotels from '../data';
import './HotelDetail.css';

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    if (!hotel) {
        return <p>Hotel not found</p>;
    }

    return (
        <section className="hotel-detail">
            <div className="back-link-container">
                <Link to="/" className="back-link">Back to list</Link>
            </div>
            <div className="hotel-header">
                <h2 className="hotel-name">{hotel.name}</h2>
                <img src={hotel.image} alt={hotel.name} className="hotel-image" />
            </div>
            <div className="hotel-info">
                <p className="hotel-location"><strong>Location:</strong> {hotel.location}</p>
                <p className="hotel-category"><strong>Local category:</strong> {hotel.rating}</p>
                <p className="hotel-price"><strong>Price:</strong> {hotel.price}</p>
                <p className="hotel-description"><strong>Description:</strong> {hotel.description}</p>
            </div>
        </section>
    );
};

export default HotelDetail;
