import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  // Ensure the CSS file is correctly imported
import hotels from '../data'; // Import the hotels data

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <div className="grid" style={{ display: 'flex', flexDirection: 'row' }}>
            {/* Main image on the left */}
            <h1 className="title-large">{hotel.name}</h1>
            <div className="hero-image-container-hotel-widok-1" style={{ width: '50%', height: '80vh', backgroundImage: `url(${hotel.image})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>
                {/* Background image for a better full cover effect */}
            </div>

            {/* Hotel details on the right */}
            <div className="hero-details" style={{ width: '50%', padding: '20px', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                <div>

                    <p>Location: {hotel.location}</p>
                    <p>{hotel.description}</p>
                    <p>Price per night: {hotel.price}</p>
                    <p>Rating: {hotel.rating} stars</p>
                </div>

                {/* Additional images container */}
                <div style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px' }}>
                    <img src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover' }} />
                    <img src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover' }} />
                </div>
            </div>
        </div>
    );
}

export default HotelDetail;
