import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  // Ensure the CSS file is correctly imported
import hotels from '../data'; // Import the hotels data

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <div className="grid">
            {/* Left side main image */}
            <div className="hero-image-container-hotel-widok-1" style={{ backgroundImage: `url(${hotel.image})`, backgroundSize: 'cover', height: '100vh' }}>
                {/* Using a div with background image for a better full cover effect */}
            </div>

            {/* Right side hotel details and smaller images */}
            <div className="hero-details" style={{ paddingLeft: '20px', paddingTop: '20px', maxWidth: '500px', backgroundColor: 'white' }}>
                <h1 className="title-large">{hotel.name}</h1>
                <div className="text-middle">
                    <h2>Location: {hotel.location}</h2>
                    <p>Description: {hotel.description}</p>
                    <p>Price per night: {hotel.price}</p>
                    <p>Rating: {hotel.rating} stars</p>
                </div>

                {/* Additional images container */}
                <div className="hero-cards" style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px' }}>
                    <img src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover' }} />
                    <img src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover' }} />
                </div>
            </div>
        </div>
    );
}

export default HotelDetail;
