import React from 'react';
import { useParams } from 'react-router-dom';
import '../App.css';
import hotels from '../data';

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <>
            <section id="hero" className="hero-welcome">
                <p className="title-large">{hotel.name}</p>
            </section>
            <div className="grid hotel-detail-layout">

                <div className="hotel-image-container" style={{ backgroundImage: `url(${hotel.image})` }}></div>

                <div className="hotel-info">
                    <p><strong>Location:</strong> {hotel.location}</p>
                    <p><strong>Local category:</strong> {hotel.rating} stars</p>
                    <p><strong>Price per night:</strong> {hotel.price}</p>
                    <p><strong>Description:</strong> {hotel.description}</p>
                </div>

                <div className='hotel-images'>
                    <img src={hotel.image} alt={hotel.name} className='hotel-image' />
                    <img src={hotel.image} alt={hotel.name} className='hotel-image' />
                </div>
            </div>
        </>
    );
}

export default HotelDetail;
