import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  // Ensure the CSS file is correctly imported
import hotels from '../data'; // Import the hotels data

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (


        <div className="grid" style={{ display: 'flex', flexDirection: 'row' }}>

            <div className="hero-image-container-hotel-widok-1" style={{ width: '50%', height: '50', backgroundImage: `url(${hotel.image})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>

            </div>

            <div className="text-s" style={{ width: '50%', padding: '20px', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                <div>
                    <p><strong>Location:</strong> {hotel.location}</p>
                    <p><strong>Description:</strong> {hotel.description}</p>
                    <p><strong>Price per night:</strong> {hotel.price}</p>
                    <p><strong>Rating:</strong> {hotel.rating} stars</p>
                </div>


                <div className='image-card' style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px'}}>
                    <img className='image-card' src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover',borderRadius:'32px' }} />
                    <img className='image-card' src={hotel.image} alt={hotel.name} style={{ width: '48%', height: '200px', objectFit: 'cover' ,borderRadius:'32px'}} />
                </div>
            </div>
        </div>
    );
}

export default HotelDetail;
