import React from 'react';
import { useParams } from 'react-router-dom';

import '../App.css';  // Ensure the CSS file is correctly imported
import hotels from '../data'; // Import the hotels data

const HotelDetail = () => {
    const { id } = useParams();
    const hotel = hotels.find(hotel => hotel.id === parseInt(id));

    return (
        <div className="grid">
            {/* Left side main image */}
            <div className="hero-image-container-hotel-widok-1" >
            <img src={hotel.image} alt={hotel.name} />
            </div>

            {/* Right side hotel details and smaller images */}
            <div className="hero-details">
                <h1 className="title-large">{hotel.name}</h1>
                <div className="text-middle">
                    <h2>Location: {hotel.location}</h2>
                    <p>Description: {hotel.description}</p>
                    <p>Price per night: {hotel.price}</p>
                    <p>Rating: {hotel.rating} stars</p>
                </div>

                {/* Additional images container */}
                <div className="hero-cards">
                    {/* Adjust the image source paths as necessary */}
                    <div className="card-image" style={{ backgroundImage: `url(${hotel.additionalImages[0]})` }}><img src={hotel.image} alt={hotel.name} /></div>
                    <div className="card-image" style={{ backgroundImage: `url(${hotel.additionalImages[1]})` }}></div>
                </div>
            </div>
        </div>
    );
}

export default HotelDetail;
